document.querySelector("div");chrome.runtime.onMessage.addListener(({event:r,time:o})=>{const a=document.querySelectorAll("video,audio");switch(r){case"play_videos":a.forEach(e=>{e.play()});break;case"pause_videos":a.forEach(e=>{e.pause()});break;case"mute_videos":a.forEach(e=>{e.muted=!0});break;case"unmute_videos":a.forEach(e=>{e.muted=!1});break;default:const t=document.createElement("audio");t.src=chrome.runtime.getURL("clock.mp3");const c=setInterval(()=>{t.play().catch(()=>{})},1e3);t.addEventListener("play",()=>clearInterval(c));break}return!0});
